<script lang="ts" setup>
  import { ref } from 'vue';
  import { useMessage } from '/@/hooks/web/useMessage';

  const { createMessage } = useMessage();
  const progress = ref(0);
  const formatPercent111 = ref(false);
  const testvalue = ref('');
  const testvalue1 = ref('');

  const group = ref(false);
  const showimg = ref(false);
  const rlpss = ref(false);

  const chengdian = ref(false);

  const jiaoban = ref(false);

  const numvalue = ref(''); // 输入框的值，初始为空字符串
  const test = ref(false);

  const totalTimeInMinutes = 10; // 设定总时间为10分钟
  const currentTimeInMinutes = ref(0);
  const currentPercent = ref(0);
  const shui = ref(false);
  const daoshui = ref(false);
  const ctext = ref(true);
  const keli = ref(true);
  const rlp = ref(true);
  const LT = ref(true);
  const SG = ref(false);
  const an = ref(false);

  let timer = null;
  const showfm = () => {
    showimg.value = !showimg.value;
    const randomNum = 100; // 生成一个 0 到 999 的随机数
    numvalue.value = randomNum.toString(); // 将随机数转换为字符串并设置为输入框的值
  };
  // 调用节流函数
  const start = throttle(() => {
    daoshui.value = true;
    ctext.value = false;
    keli.value = false;
    setTimeout(() => {
      shui.value = true;
    }, 2000);
    setTimeout(() => {
      daoshui.value = false;
    }, 4000);
    setTimeout(() => {
      chengdian.value = !chengdian.value;
    }, 4000);
  }, 5000); // 5000毫秒的节流间隔

  const start1 = throttle(() => {
    rlp.value = false;
    group.value = true;
    formatPercent111.value = true;
    test.value = true;
    // animateProgress();
  }, 10000); // 5000毫秒的节流间隔
  const animateProgress = () => {
    const duration = 10; // 动画持续时间（秒）
    const fps = 10; // 每秒的帧数
    const totalFrames = duration * fps; // 总帧数
    const increment = 100 / totalFrames; // 每帧增加的百分比

    let currentFrame = 0;

    const progressInterval = setInterval(() => {
      currentFrame++;
      progress.value += increment;

      if (currentFrame >= totalFrames) {
        clearInterval(progressInterval);
        createMessage.success('溶解完毕');
      }
    }, 1000 / fps);
  };

  const ultrasonic = throttle(() => {
    an.value = true;
    // animateLiquid();
  }, 10000); // 5000毫秒的节流间隔

  function begintest() {
    SG.value = true;
    animateLiquid();
  }
  function begintest1() {
    formatPercent111.value = true;
    console.log(`output->testvalue`, testvalue.value);
    startTimer();
  }
  // function standby() {
  //   waterafter.value = false;
  //   test.value = !test.value;
  //   water.value = !water.value;
  //   saizi.value = false;
  //   formatPercent111.value = false;
  //   group.value = false;
  // }
  function seal() {
    SG.value = false;
    an.value = false;
    rlpss.value = true;
    const svgIcon = document.querySelector('.ccc');
    svgIcon.classList.toggle('rotated');
    moveElementToPosition(svgIcon, -12, 50); // 将元素移动到 (200, 300) 的位置
    const path = document.getElementById('tmd');
    var currentOpacity = parseFloat(path.getAttribute('opacity'));

    // 定义透明度每秒减小的速度
    const fadeSpeed = 0.1;

    // 定义每秒执行一次的函数
    const fadeInterval = setInterval(function () {
      if (currentOpacity > 0.3) {
        currentOpacity -= fadeSpeed;
        path.setAttribute('opacity', currentOpacity.toFixed(1)); // 更新透明度
      } else {
        clearInterval(fadeInterval); // 透明度为0时停止动画
      }
    }, 1000); // 每秒执行一次
  }
  function moveElementToPosition(element, top, left) {
    element.style.position = 'absolute'; // 或者 'relative'
    element.style.top = top + '%';
    element.style.left = left + '%';
  }
  function remoave() {
    console.log(`output->111`, 111);
    const svgElement = document.getElementById('saizi');
    svgElement.style.left = '48.75%'; // 将SVG元素向右移动10像素
    svgElement.style.top = '8%'; // 将SVG元素向下移动20像素
    // 添加定时器
    setTimeout(function () {
      console.log('定时器执行：显示一句话');
      // 这里替换成你想要显示的具体内容
      createMessage.success('密封完毕，静置待使用');
    }, 1000); // 这里的1000表示延迟执行的时间，单位是毫秒，这里设置为1秒钟后执行
  }
  //节流
  function throttle(func, delay) {
    let timer = null;
    return function () {
      if (!timer) {
        func();
        timer = setTimeout(() => {
          timer = null;
        }, delay);
      }
    };
  }

  const startTimer = () => {
    timer = setInterval(() => {
      currentTimeInMinutes.value++;
      currentPercent.value = Math.min((currentTimeInMinutes.value / totalTimeInMinutes) * 100, 100);
      if (currentPercent.value >= 100) {
        clearInterval(timer);
        createMessage.success('已完成超声！');
      }
    }, 1000); // 每秒执行一次
  };

  const formatPercent = (percent) => {
    return `${percent.toFixed(0)} %`;
  };

  function animateLiquid() {
    const liquidPath = document.getElementById('tt');
    const data = testvalue1.value;
    console.log(`output->data`, data);
    // 定义动画持续时间（单位：毫秒）
    const animationDuration = 6000; // 5秒钟
    // 定义起始值和结束值
    const startH = 950; // 初始水平值
    const endH = 950 - data; // 结束水平值
    const startV = 0; // 初始垂直值
    const endV = 0 + data; // 结束垂直值

    // 获取动画开始时间
    let start = null;

    // 定义动画函数
    function step(timestamp) {
      if (!start) start = timestamp;
      const progress = timestamp - start;

      // 计算当前时间对应的参数值
      const currentH = startH + (endH - startH) * (progress / animationDuration);
      const currentV = startV + (endV - startV) * (progress / animationDuration);

      // 更新液体路径的形状参数
      liquidPath.setAttribute(
        'd',
        `M419.843589 ${currentH}h184.737506v${currentV}c0 5.096207-4.246839 9.343046-9.343046 9.343047H428.761951c-5.096207 0-9.343046-4.246839-9.343046-9.343047l0.424684-347.391449z`,
      );

      // 继续执行动画，直到达到指定的持续时间
      if (progress < animationDuration) {
        requestAnimationFrame(step);
      }
    }

    // 启动动画
    requestAnimationFrame(step);
  }
